    private void iniciarButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_iniciarButtonActionPerformed
        try{
            ServerInterface server = (ServerInterface) Naming.lookup("//" + "localhost" + "/adivinaquien");
            boolean playerIsChallenger = server.startGame("player", false);
            if (playerIsChallenger){
                Challenger challenger = new Challenger(new Character());
                // server.bindChallenger(challenger);
                challenger.characterWindow.setVisible(true);
            } else{
                new Game().setVisible(true);
            }
        }catch (Exception e) {
            System.out.println("Error en el cliente: " + e.getMessage());
            e.printStackTrace();
        }
    }//GEN-LAST:event_iniciarButtonActionPerformed

